    validate({
      rules: {
        name (name, fieldName, state) {
          if (name !== state.values.username) {
            return
          }

          return {
            name: 'Username and name cannot be equal!',
            username: 'Username and name cannot be equal!'
          }
          }
      }
    })
  ]
})

